import { useEffect, useState } from "react"

export default function Card(props) {

    const [torf, toggle] = useState(false);
    const[inputs,inc]=useState({inputs:[]})

    console.log(inputs.inputs.length)

    useEffect(() => {
        if (torf) {
            document.getElementById(`menu-${props.elem}`).style.display = 'block'
        }
        else {
            document.getElementById(`menu-${props.elem}`).style.display = 'none'
        }
    }, [torf])

    function addmore(){
        inc(prev=>{let temp=[...prev.inputs]

            temp.push(<input/>)
            return {...prev,inputs:temp};
        })
    }


    return (
        <>

            <div className="btn-group" style={{ display: 'flex', flexDirection: 'column' }}>
                <button onClick={() => { toggle(prev => !prev) }} type="button" className="btn btn-info dropdown-toggle mb-3" >
                    {props.elem}
                </button>

                <div id={`menu-${props.elem}`} style={{ display: 'none', marginBottom: '1rem',background:'blue' }}>
                    <div style={{display:'flex',flexDirection:'column',background:'green',width:'300%'}}>
                        <button onClick={addmore} style={{ borderStyle: 'solid',borderRadius:'2rem',width:'fit-content',marginLeft:'auto',marginRight:'auto',marginBottom:'1rem',padding:'0.5rem 1.5rem' }}>Add meal</button>

                        <form style={{display:'flex',marginLeft:'auto',marginRight}} class="form-inline">
  <div class="form-group mb-2">
    <label for="staticEmail2" class="sr-only">Email</label>
    <input type="text" readonly class="form-control-plaintext" id="staticEmail2" value="email@example.com"/>
  </div>
  <div class="form-group mx-sm-3 mb-2">
    <label for="inputPassword2" class="sr-only">Password</label>
    <input type="password" class="form-control" id="inputPassword2" placeholder="Password"/>
  </div>
  <button type="submit" class="btn btn-primary mb-2">Confirm identity</button>
</form>
                        {(inputs.inputs.length!==0) && inputs.inputs.map((elem,index)=><div key={index}>{elem}</div>)}

                    </div>

                </div>

            </div>
        </>
    )
}
