import { useEffect, useState } from "react"

export default function Card(props) {

    const [torf,toggle]=useState(false);

    useEffect(()=>{
        if(torf)
        {
            document.getElementById(`menu-${props.elem}`).style.display='block'
        }
        else{
            document.getElementById(`menu-${props.elem}`).style.display='none'
        }
    },[torf])


    return (
        <>

            <div className="btn-group" style={{display:'flex',flexDirection:'column'}}>
                <button onClick={()=>{toggle(prev=>!prev)}} type="button" className="btn btn-danger dropdown-toggle" >
                    Action
                </button>

                <div id={`menu-${props.elem}`} style={{display:'none',marginLeft:'auto',marginRight:'auto',marginTop:'2rem',margin}}>
                    <input />
                </div>

            </div>
        </>
    )
}
