import { useEffect, useState } from "react"
import { Link } from "react-router-dom"
import Card from "./card"
import '../App.css'

export default function Admin() {

    const [allUsers, set] = useState([])

    const [user,change]= useState();

    console.log(allUsers)

    useEffect(() => {
        fetch('http://localhost:8000/admin').then(data => data.json()).then(data => set(data.allUsers))
    }, [])

    console.log(user)


    return (
        <div style={{background:"linear-gradient(black, skyblue)",minHeight:'90vh'}}>{localStorage.getItem('auth-token-admin')  &&
            <div style={{ width: '95%', margin: '0 auto' }}>



                <div style={{ display: 'flex'}}>
                <div style={{fontSize:'2rem',fontWeight:'bolder',color:'white',padding:'2rem 0',marg}}>Users</div>

                    <table style={{borderRadius:'1rem',width:'80%',margin:'auto'}}>
                        <tbody>
                            <tr><th>User Name</th>
                                <th>Userid</th>
                                </tr>
                                </tbody>
                                <tbody>
                            {
                                allUsers.length && allUsers.map((user,index) => (
                                    <tr key={user._id}><td><div id="link" onClick={()=>{
                                        change(()=>{
                                            return user;
                                        })
                                    }} >{user.first} {user.last}</div></td><td>{user.userid}</td>

                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>



                </div>





            </div> || (!localStorage.getItem('auth-token-admin') && <div>Admin needs to login first <Link className="btn btn-primary" to={'/adminlogin'}>Login</Link></div>)}

            {  <div style={{position:'absolute',background:'white',display:!user && 'none',top:'10vh',left:'10%',right:'10%',height:'90vh',width:'80%'}}>{ user && <Card elem={user} />}</div>}
        </div>
    )
}
