import { useEffect, useState } from "react"
import { Link } from "react-router-dom"
import Card from "./card"

export default function Admin() {

    const [allUsers, set] = useState([])

    console.log(allUsers)

    useEffect(() => {
        fetch('http://localhost:8000/admin').then(data => data.json()).then(data => set(data.allUsers))
    }, [])



    return (
        <>{localStorage.getItem('auth-token') && 
        <div style={{width:'95%',margin:'2rem auto'}}>
        <div style={{display:'flex',flexDirection:'column'}}>
            {

                allUsers.length && allUsers.map((user) => (
                  <Card key={user._id} elem={user}/>
                ))

            }
        </div>
        </div> || (!localStorage.getItem('auth-token') && <div>Admin needs to login first <Link style={{ fontSize: '1rem', padding: '1px 1rem', marginBottom: '0.5rem' }} to={'/adminlogin'}>Login</Link></div>)}
        </>
    )
}
