import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";


export default function Login() {

    const [logincreds, change] = React.useState({ userid: '', password: '', admin: false })



    const navigate = useNavigate();

    console.log(logincreds)

    function handlechange(e) {

        change(prev => {
            let temp = { ...prev }
            temp[e.target.name] = e.target.value;

            return temp;
        })

    }

    function handleclick(e) {

        e.preventDefault()
        fetch('http://localhost:8000/login',{
            method:'POST',
            headers:{
                'Content-Type':'application/json',
            },
            body:JSON.stringify({data:logincreds})
        }).then(data=>data.json()).then(data=>console.log(data))

    }

    useEffect(()=>{

        if(logincreds.admin){

        document.getElementById('admin').style.backgroundColor='black'

        document.getElementById('admin').style.font='white'

        document.getElementById('user').style.backgroundColor='royalBlue'

        document.getElementById('user').style.font='white'

        }
        else {

            document.getElementById('user').style.backgroundColor='black'

        document.getElementById('user').style.font='white'

        document.getElementById('admin').style.backgroundColor='royalBlue'

        document.getElementById('admin').style.font='white'

        }

    },[logincreds.admin])

    function handleadmin(e){

        e.preventDefault()

        change(prev=>{return ({...prev,admin:true})})



    }

    function handleuser(e){

        e.preventDefault()

        change(prev=>{return ({...prev,admin:false})})

        document.getElementById('user').style.backgroundColor='black'

        document.getElementById('user').style.font='white'

    }




    return (
        <>
            <form style={{ width: '50%', marginLeft: 'auto', marginRight: 'auto', marginTop: '80px' }}>
                <div style={{ fontSize: '4rem', textAlign: 'center', fontFamily: 'sans-serif' }}>LOGIN</div>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                    <input onChange={handlechange} name='userid' type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                    <input onChange={handlechange} name="password" type="password" className="form-control" id="exampleInputPassword1" />
                </div>

                <button onClick={handleclick} className="btn btn-primary">Submit</button>
                <div style={{ display: 'flex', flexDirection: 'row', marginTop: '1rem', width: '30%', marginLeft: 'auto', marginRight: 'auto', justifyContent: 'space-around' }}>
                    <button onClick={handleadmin} id='admin' className="btn btn-primary">Admin</button>
                    <button onClick={handleuser} id="user" className="btn btn-primary">User</button>
                </div>
            </form>


        </>
    )
}
