import { useEffect, useState } from "react"

export default function Card(props) {

    const [torf, toggle] = useState(false);
    const [input, change] = useState({ title: '', content: '' })

    const [count, cnt] = useState(0)
    const [meals, set] = useState([])





    console.log(props)

    useEffect(() => {
        if (torf) {
            document.getElementById(`menu-${props.elem._id}`).style.display = 'block'
        }
        else {
            document.getElementById(`menu-${props.elem._id}`).style.display = 'none'
        }
    }, [torf])



    function handlechange(e) {
        e.preventDefault()
        change(prev => {
            let temp = { ...prev };

            temp[e.target.name] = e.target.value;

            return temp;
        })
    }

    function handlesubmit(e) {

        e.preventDefault()
        fetch('http://localhost:8000/addmeals', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ data: input, userid: props.elem._id })
        })
    }


    useEffect(() => {
        fetchdata()
    }, [])



    function fetchdata() {
        fetch('http://localhost:8000/admin/getmeals', {

            headers: {
                'id': props.elem._id
            }
        }).then(data => data.json()).then((data) => set(data))

    }


    console.log(meals)





    return (
        <div >

            <div className="btn-group" style={{ width: 'fit-content', display: 'flex' }}>
                <button onClick={() => { toggle(prev => !prev) }} type="button" className="btn btn-info dropdown-toggle mb-3" >
                    {props.elem.first}{props.elem.last}
                </button>

            </div>

            <div id={`menu-${props.elem._id}`} style={{ display: 'none', marginBottom: '1rem' }}>
                <div style={{ width: '80%', display: 'flex', margin: '0 auto', display: 'flex', flexDirection: 'column' }}>



                    <form >
                        <div className="form-group">
                            <label className="ms-1" htmlFor="formGroupExampleInput">Meal Title</label>
                            <input type="text" onChange={handlechange} name='title' className="form-control" id="formGroupExampleInput" placeholder="Example input" />
                        </div>
                        <div className="form-group">
                            <label className="ms-1" htmlFor="formGroupExampleInput2">Meal Content</label>
                            <input type="text" onChange={handlechange} name="content" className="form-control" id="formGroupExampleInput2" placeholder="Another input" />
                        </div>
                        <button onClick={handlesubmit} type="button" className="btn btn-primary mt-2">Add</button>

                    </form>


                    <div className="mt-2 dropdown">
                        <button onClick={fetchdata} className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                            All Meals
                        </button>
                        <ul style={{ maxHeight: '250px',width:'100%', overflow: 'auto' }} className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <ol style={{backgroundColor:'gray'}} className="list-group list-group-numbered">



                                {meals.length && meals.map((elem, index) => <li key={index} className="list-group-item d-flex justify-content-between align-items-start">
                                    <div className="ms-2 me-auto">
                                        <div className="fw-bold">{elem.allmeals.title}</div>
                                        {elem.allmeals.content}
                                    </div>

                                </li>
                                )
                                }
                            </ol>
                        </ul>
                    </div>


                </div>

            </div>


        </div>
    )
}
