import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

export default function User(){

    const [meals,set]=useState([])

    const[username,name]=useState('');




    const [d,dc]=useState('');

    console.log(username)
    useEffect(()=>{

        fetch('http://localhost:8000/getmeals',{
            method:'POST',
            headers:{
                'Content-Type': 'application/json',
                'auth-token':localStorage.getItem('auth-token'),

            },
            body:JSON.stringify({mealdate:d,kk:'kk'})

        }).then(data=>data.json()).then(data=>{set(data.meals);
        name(data.user.first)}).then(console.log('ff'))
    },[d])

    return( <div  style={{display:'flex'}}>

      <div style={{width:'100%'}}>

       <div onChange={(e)=>{dc(e.target.value)}} style={{textAlign:'center',fontSize:'1.25rem',padding:'2rem 0'}}> 
       <div style={{}}><h1 style={{padding:'1rem 0',color:'white',textTransform:'capitalize'}}>Good {((new Date().getHours())>=18  && 'Evening') || (new Date().getHours>=5 && 'Morning') || (new Date().getHours()>=12 && 'Afternoon')}, {username}</h1></div>
       <input type="date" /></div>
<div style={{width:'80%',height:'100vh',margin:'0 auto'}}>
        {localStorage.getItem('auth-token') && ( meals.length ? <div><table className="table" style={{background:'white',borderRadius:'1rem'}}>
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Time</th>
      <th scope="col">Meal</th>
      <th scope="col">Date</th>

    </tr>
  </thead>
  <tbody>
    {meals.map((elem,index)=><tr key={index}>
      <th scope="row">{index+1}</th>
      <td >{elem.allmeals.title}</td>
      <td >{elem.allmeals.content}</td>
      <td >{elem.mealdate}</td>
    </tr>)}


  </tbody>
</table></div>
        :'No data to show')}
        {!localStorage.getItem('auth-token') &&

        <div >You need to login first <Link className="btn btn-primary" to={'/login'}>Login</Link></div>}

        </div></div>
        </div>
    )

}
