import { useEffect, useState } from "react"

export default function Card(props) {

    const [torf, toggle] = useState(false);
    const [inputs, inc] = useState({ inputs: [] })

    console.log(inputs.inputs.length)

    useEffect(() => {
        if (torf) {
            document.getElementById(`menu-${props.elem}`).style.display = 'block'
        }
        else {
            document.getElementById(`menu-${props.elem}`).style.display = 'none'
        }
    }, [torf])

    function addmore() {
        inc(prev => {
            let temp = [...prev.inputs]

            temp.push(<input />)
            return { ...prev, inputs: temp };
        })
    }


    return (
        <div style={{width:'80%',ma}}>

            <div className="btn-group" style={{ width: 'fit-content', display: 'flex' }}>
                <button onClick={() => { toggle(prev => !prev) }} type="button" className="btn btn-info dropdown-toggle mb-3" >
                    {props.elem}
                </button>

            </div>

            <div id={`menu-${props.elem}`} style={{ display: 'none', marginBottom: '1rem' }}>
                <div style={{width: '35%', display: 'flex', margin: '0 auto', display: 'flex', flexDirection: 'column' }}>
                    <button onClick={addmore} style={{ borderStyle: 'solid', borderRadius: '2rem', width: 'fit-content', marginLeft: 'auto', marginRight: 'auto', marginBottom: '1rem', padding: '0.5rem 1.5rem' }}>Add meal</button>


                    <form >
                        <div className="form-group">
                            <label className="ms-1" htmlFor="formGroupExampleInput">Meal Title</label>
                            <input type="text" className="form-control" id="formGroupExampleInput" placeholder="Example input" />
                        </div>
                        <div className="form-group">
                            <label className="ms-1" htmlFor="formGroupExampleInput2">Meal Content</label>
                            <input type="text" className="form-control" id="formGroupExampleInput2" placeholder="Another input" />
                        </div>
                    </form>


                    {(inputs.inputs.length !== 0) && inputs.inputs.map((elem, index) => <div key={index}>{elem}</div>)}

                </div>

            </div>


        </div>
    )
}
