import React, { useContext, useEffect, useState } from "react";
import { useNavigate,Link } from "react-router-dom";
import background from './images/bg3.jpg'
import formContext from '../context/form/formcontext';




export default function Login() {



    const a=useContext(formContext)

    console.log(a)


    const [logincreds, change] = React.useState({ userid: '', password: ''})



    const navigate = useNavigate();

    console.log(logincreds)

    function handlechange(e) {

        change(prev => {
            let temp = { ...prev }
            temp[e.target.name] = e.target.value;

            return temp;
        })

    }

    function handleclick(e) {

        e.preventDefault()
        fetch('http://localhost:8000/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ data: logincreds })
        }).then(data => data.json()).then(data => {
            if (data.success) {
                localStorage.setItem('auth-token', data.authtoken)


                a.inc(prev=>prev+1)
                navigate('/user')

            }
        })

    }



    function handleadmin(e) {

        e.preventDefault()

        change(prev => { return ({ ...prev, admin: true }) })



    }






    return (
        <div style={{ minheight:'80vh',height:'100vh',margin:'0 auto' }}>
            {}



        </div>
    )
}
